form.script:
var is_changed = false;
var txt = 'NoData';
$(function(){

    $('select[name="fin_objective"]').change(function(){
        confirmationFunction().done(function(){
            dataStamp().done(function(){
                console.log($('textarea[name="objective"]').val());
                $('textarea[name="objective"]').focus().change();
            });
        })
    })
})

function confirmationFunction(){
    var confirmation_dfrd = $.Deferred();
    var detail_change = confirm("เปลี่ยนรายละเอียดวัตถุประสงค์ตามการตั้งค่าหรือไม่");
    if (detail_change == true) {
        is_changed = true;
        confirmation_dfrd.resolve();
    }
    return confirmation_dfrd.promise();
}
function dataStamp(){
    var data_drfd = $.Deferred();
    var checkData = setInterval(function() {
       if($('textarea[name="objective_changed"]').val()!=''){
            txt = $('textarea[name="objective_changed"]').val();
            $('textarea[name="objective"]').val(txt);
            clearInterval(checkData);
            data_drfd.resolve();
       }
    }, 100);
    return data_drfd.promise();
}
